    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed

        command_parser cp = new command_parser();
        cp.assign_marks(copy, jTextField1.getText());
        jLabel3.setText("The marks for this copy has been sent successfully.");
        copy = "";
        jTextField1.setText("");

        // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        command_parser cp = new command_parser();

        copy = cp.evaluate_copies(professor_id_global);

        if (copy== ""){

                jLabel3.setText("All copies assigned to you have been checked.");

        }
        Desktop desktop = Desktop.getDesktop();
        File file = new File(copy);
        try{
            if(file.exists()) desktop.open(file);
        } catch(Exception e){

            System.out.println(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

